mapper.select:

select count(*)
from product p, orders o, ordersitem oi
where o.orders_id = oi.orders_id and oi.product_id = p.product_id and o.id = #{id}


select *
from (
	select p.*, o.*, oi.ordersItem_id, oi_number, oi_color, oi_size, ROW_NUMBER() OVER(ORDER BY o.o_enroll, o.orders_id DESC) as rNUM
	from product p, orders o, ordersitem oi
	where o.orders_id = oi.orders_id and oi.product_id = p.product_id and o.id = #{id}
	) odr
WHERE odr.rNUM BETWEEN #{perPageNum}*(#{page}-1) + 1 AND #{perPageNum}*(#{page}-1)+${perPageNum} 
ORDER BY rNUM desc
